mapper.select:
select * 
from board 
order by indate desc;

mapper.update:
update board
set count = count + 1
where idx = #{idx}

update board 
set title = #{title}, contents = #{contents}
where idx = #{idx}

mapper.select:
select * from board
where title like concat('%',#{keyword},'%')
order by indate desc limit #{skip}, #{amount}
